mapper.select:
select menu_no
from menu
where menu_name=#{menu_name}

select distinct menu_no
from menu
where menu_name like #{keyword} or menu_description like #{keyword} or menu_reqMaterial like #{keyword}

mapper.update:
update menu_stat
set menu_readCnt = menu_readCnt+1
where menu_no = #{menu_no}

mapper.delete:
delete ms, m
from menu_stat ms, menu m
where m.menu_no=ms.menu_no and m.menu_no = #{menu_no}
